import { FcDeleteDatabase } from "react-icons/fc";
import { useContext } from "react";
import { CartContext } from "../redux/Context/CartContext";
import { toast } from "react-hot-toast";
import { Box, Paper } from "@mui/material";

const CartItem = ({ item, itemIndex }) => {
  const { removeCartItem } = useContext(CartContext);

  const removeFromCart = () => {
    removeCartItem(item.id);
    toast.success("Item Removed");
  };

  return (
   <Box sx={{display:"flex" , justifyContent:"center" , alignItems:"center" , height:"100%" , background:"none !important"}}>
    <Paper sx={{width:"20em" , height:"20em" , background:"#121212" , display:"flex" , justifyContent:"center" , alignItems:"center"}}>
           <img src={item.image} alt="" width={300} />
    </Paper>

   </Box>
  );
};

export default CartItem;
