import { FaShoppingCart } from "react-icons/fa";
import { useContext } from "react";
import { NavLink } from "react-router-dom";
import { CartContext } from "../redux/Context/CartContext";
import { AppBar, Box, Typography } from "@mui/material";
import logo from './Images/8093572-removebg-preview.png'
const Navbar = () => {
  const { cart } = useContext(CartContext);

  return (
    <div>
      <AppBar elevation={2}  sx={{display:"flex", justifyContent:"space-around", alignItems:"center", background:"#252525" , padding:"0.5em 1em"}}>
        <Box sx={{display:"flex", justifyContent:"space-around", alignItems:"center",}}>
        <Box sx={{display:"flex", alignItems:"center"}}>
        <img src={logo} alt="" width={100}/>
        <Typography sx={{fontWeight:"700" , fontSize:"19px" , position:"relative" , right:"1.3em"}}>Shopify</Typography>
        </Box>
        <NavLink to='/'>
        <Typography  sx={{fontWeight:"700" , fontSize:"19px"}}>Home</Typography>
        </NavLink>
        
        </Box>
      </AppBar>
    </div>
  );
};

export default Navbar;
