import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../redux/Context/CartContext";
import CartItem from "../components/CartItem";
import { Paper, Typography, Button } from "@mui/material";
import carting from "../components/Images/pngwing.com.png";

const Cart = () => {
  const { cart } = useContext(CartContext);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        height: "100%",
      }}
    >
      {cart.length > 0 ? (
        <div
          style={{
            padding: "2em 1em",
            background: "#121212",
            display: "flex",
            flexDirection: "column",
            gap: "1em",
          }}
        >
          {cart.map((item, index) => {
            return (
              <CartItem key={item.id} item={item} itemIndex={index} />
            );
          })}
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              color: "#fff",
              gap: "0.5em",
            }}
          >
            <Typography variant="h6">Cart Summary</Typography>
            <p>
              <span>Total Items: {cart.length}</span>
            </p>
            <Typography variant="body1">
              Total Amount: ${totalAmount}
            </Typography>
            <Button
              variant="contained"
              sx={{
                background: "#fff !important",
                color: "#121212",
                fontWeight: "700",
              }}
            >
              CheckOut Now
            </Button>
          </div>
        </div>
      ) : (
        <Paper
          elevation={3}
          sx={{
            background: "#121212",
            width: { sm: "22em", xs: "auto" },
            padding: "1.5em 1.5em",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              gap: "1em",
            }}
          >
            <img src={carting} alt="" width={300} />
            <Typography
              sx={{ color: "#fff", fontWeight: "700", fontSize: "19px" }}
            >
              Cart Empty
            </Typography>
            <Typography
              sx={{
                color: "#fff",
                fontWeight: "500",
                fontSize: "16px",
                width: "17em",
                textAlign: "center",
              }}
            >
              Look like you haven't added anything in your cart!!!
            </Typography>
            <Link to={"/"}>
              <Button
                variant="contained"
                sx={{
                  background: "#fff !important",
                  color: "#121212",
                  fontWeight: "700",
                }}
              >
                Shop Now
              </Button>
            </Link>
          </div>
        </Paper>
      )}
    </div>
  );
};

export default Cart;
