import { useContext } from "react";
import { CartContext } from "../redux/Context/CartContext";
import { Button, Typography, Grid , Box} from "@mui/material";
import { toast } from "react-hot-toast";

const Product = ({ post }) => {
  const { cart, addCartItem, removeCartItem } = useContext(CartContext);

  const addToCart = () => {
    addCartItem(post);
    toast.success("Item added to Cart");
  };

  const removeFromCart = () => {
    removeCartItem(post.id);
    toast.error("Item removed from Cart");
  };

  return (
    <Grid item xs={12} sm={6} md={4} lg={3} sx={{gap:"1em" , display:"flex"}}>
      <Box sx={{display:"flex" , justifyContent:"center" , flexDirection:"column" , alignItems:"center" , width:"auto", minWidth:"20em",minHeight:"25em" , height:"auto" , background:"pink"}}>
        <div>
          <Typography variant="h6" className="text-700 font-semibold text-lg text-left truncate w-40 mt-1" style={{ color: "#fff" }}>
            {post.title}
          </Typography>
        </div>
        <div>
          <Typography variant="body2" className="w-40 font-normal text-[10px] text-left" style={{ color: "rgba(255, 255, 255, 0.7)" }}>
            {post.description.split(" ").slice(0, 10).join(" ") + "..."}
          </Typography>
        </div>
        <div className="h-[180px]">
          <img src={post.image} className="h-full w-full" alt="Product" />
        </div>
          <div>
            <Typography variant="subtitle1" className="text-green-600 font-semibold">
              ${post.price}
            </Typography>
          </div>

          {cart.some((p) => p.id === post.id) ? (
            <Button
              variant="outlined"
              className="text-gray-700 border-2 border-white-700 rounded-full font-semibold text-[12px] p-1 px-3 uppercase hover:bg-gray-700 hover:text-white transition duration-300 ease-in"
              onClick={removeFromCart}
            >
              Remove Item
            </Button>
          ) : (
            <Button
              variant="outlined"
              className="text-gray-700 border-2 border-white-700 rounded-full font-semibold text-[12px] p-1 px-3 uppercase hover:bg-gray-700 hover:text-white transition duration-300 ease-in"
              onClick={addToCart}
            >
              Add to Cart
            </Button>
          )}
      </Box>
    </Grid>
  );
};

export default Product;
