import { FcDeleteDatabase } from "react-icons/fc";
import { useContext } from "react";
import { CartContext } from "../redux/Context/CartContext";
import { toast } from "react-hot-toast";
import { Box, IconButton, Paper, Typography } from "@mui/material";

const CartItem = ({ item, itemIndex }) => {
  const { removeCartItem } = useContext(CartContext);

  const removeFromCart = () => {
    removeCartItem(item.id);
    toast.success("Item Removed");
  };

  return (
    <>
    <Box sx={{display:"flex" , flex}}>
    <Paper sx={{width:"auto",padding:"0.5em 0.5em" , height:"auto" , background:"#121212" , display:"flex" , justifyContent:"center" , alignItems:"center" }}>
           <img src={item.image} alt="" width={300} />
          
    </Paper>
    <IconButton onClick={removeFromCart} sx={{alignSelf:"flex-end"}}>
            <FcDeleteDatabase/>
           </IconButton>
           <Typography sx={{color:"#fff" , fontWeight:"700"}}>{item.title}</Typography>
               
    </Box>
           </>
  );
};

export default CartItem;
