import { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";
import { Box , Grid, Typography ,IconButton , Rating, Button} from "@mui/material";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);

  async function fetchProductData() {
    setLoading(true);

    try{
      const res = await fetch(API_URL);
      const data = await res.json();

      setPosts(data);
    }
    catch(error) {
      console.log("Error occured");
      setPosts([]);
    }
    setLoading(false);
  }

  useEffect( () => {
    fetchProductData();
  },[])

  return (
    <div>
    {loading ? (
      <Spinner />
    ) : posts.length > 0 ? (
      <>
      <Typography sx={{fontWeight:"700" , fontSize:"35px" , marginTop:"1em" , marginLeft:"4em"}}>Recommended For You !!</Typography>
      <Box sx={{display:"flex" , justifyContent:"center" , alignItems:"center" , height:"100%" , minHeight:"100vh" , gap:"2em" , flexWrap:"wrap" , marginTop:"1.2em" }}>
        {posts.map((post) => (
          <Box key={post.id} sx={{width:"20em" , height:"auto", minHeight:"390px",display:"flex" , flexDirection:"column", position:"relative" , background:"pink"}}>
                <Box sx={{ minHeight:"250px" , minWidth:"250px" , display:"flex" , justifyContent:"center" , background:"#f6f6f6" , alignItems:"center"}}>
                  <img src={post.image} alt="" style={{width:"200px" , height:"200px" , mixBlendMode:"multiply"}} />
                </Box>
                <IconButton sx={{position:"absolute" , transform:"translate(-50% , -50%)" , right:"0" , top:"10%"}}>
                    <FavoriteBorderIcon/>
                </IconButton>
                 
                 <Typography sx={{fontWeight:"700" , fontSize:"14px" , marginTop:"0.5em" , padding:"0 1em"}}>{post.title} </Typography>
                 <Box sx={{display:"flex" , justifyContent:"space-between" , alignItems:"center" , width:"100%" , padding:"0 1em"}}>
                    <Typography sx={{fontWeight:"500" , fontSize:"14px"}}>{post.category}</Typography>
                    <Rating  name="half-rating-read" defaultValue={2.5} precision={0.5} readOnly />
                    </Box>
                    <Typography sx={{fontWeight:"900" , color:"#121212" , padding:"0 1em"}}>&euro; {post.price}</Typography>
                    <Box sx={{display:"flex",gap:"0.5em" , justifyContent:"center" , alignItems:"center" , width:"100%"}}>
                       <Button >Add to Cart</Button>
                       <Button >Remove Item</Button>
                    </Box>
          </Box>
        ))}
      </Box>
      </>
    ) : (
      <div className="flex justify-center items-center">
        <p>No Data Found</p>
      </div>
    )}
  </div>
  
  );
};

export default Home;
