import { toast } from "react-hot-toast";
import { useContext } from "react";
import { CartContext } from "../redux/Context/CartContext";

const Product = ({ post }) => {
  const { cart, addCartItem, removeCartItem } = useContext(CartContext);

  const addToCart = () => {
    addCartItem(post);
    toast.success("Item added to Cart");
  };

  const removeFromCart = () => {
    removeCartItem(post.id);
    toast.error("Item removed from Cart");
  };

  return (
        <>
        <Typography sx={{fontWeight:"700" , fontSize:{sm:"35px" , xs:"25px"} , marginTop:"1em" , marginLeft:{sm:"2em" ,xs:"0.5em"}}}>Recommended For You !!</Typography>
        <Box sx={{display:"flex" , justifyContent:"center" , alignItems:"center" , height:"100%" , minHeight:"100vh" , gap:"2em" , flexWrap:"wrap" , marginTop:"1.2em" }}>
            <Box key={post.id} sx={{width:"20em" , height:"auto", minHeight:"430px",display:"flex" , flexDirection:"column", position:"relative"}}>
                  <Box sx={{ minHeight:"250px" , minWidth:"250px" , display:"flex" , justifyContent:"center" , background:"#f6f6f6" , alignItems:"center"}}>
                    <img src={post.image} alt="" style={{width:"200px" , height:"200px" , mixBlendMode:"multiply"}} />
                  </Box>
                  <IconButton sx={{position:"absolute" , transform:"translate(-50% , -50%)" , right:"0" , top:"10%"}}>
                      <FavoriteBorderIcon/>
                  </IconButton>
                   <Box sx={{display:"flex" , flexDirection:"column" , minHeight:"7.5em"}}>
                   <Typography sx={{fontWeight:"700" , fontSize:"14px" , padding:"0 1em"}}>{post.title} </Typography>
                   <Box sx={{display:"flex" , justifyContent:"space-between" , alignItems:"center" , width:"100%" , padding:"0 1em"}}>
                      <Typography sx={{fontWeight:"500" , fontSize:"14px"}}>{post.category}</Typography>
                      <Rating  name="half-rating-read" defaultValue={2.5} precision={0.5} readOnly />
                      </Box>
                      <Typography sx={{fontWeight:"900" , color:"#121212" , padding:"0 1em"}}>&euro; {post.price}</Typography>
                      </Box>
                      <Box sx={{display:"flex",gap:"0.5em" , justifyContent:"center" , alignItems:"center" , width:"100%" }}>
                         <Button variant="contained" onClick={addToCart} sx={{background:"#023c29 !important" , color:"#fff" , borderRadius:"15px"}}>Add to Cart</Button>
                         <Button variant="outlined" onClick={removeFromCart} sx={{borderColor:"#023c29 !important" , color:"#121212" , borderRadius:"15px"}}>Remove Item</Button>
                      </Box>
            </Box>
          ))}
        </Box>
        </>
  );
};

export default Product;
