import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../redux/Context/CartContext";
import CartItem from "../components/CartItem";
import { Paper, Typography, Button } from "@mui/material";
import cart from '../components/Images/pngwing.com.png'
const Cart = () => {
  const { cart } = useContext(CartContext);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        {cart.length > 0 ? (
          <div>
            {cart.map((item, index) => {
              return <CartItem key={item.id} item={item} itemIndex={index} />;
            })}
            <div>
              <Typography variant="h5">Your Cart</Typography>
              <Typography variant="h6">Summary</Typography>
              <p>
                <span>Total Items: {cart.length}</span>
              </p>
            </div>
            <div>
              <Typography variant="body1">Total Amount: ${totalAmount}</Typography>
              <Button variant="contained">CheckOut Now</Button>
            </div>
          </div>
        ) : (
          <div>
            <Typography variant="h5">Cart Empty</Typography>
            <Link to={"/"}>
              <Button variant="contained">Shop Now</Button>
            </Link>
          </div>
        )}
      </Paper>
    </div>
  );
};

export default Cart;
