import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../redux/Context/CartContext";
import CartItem from "../components/CartItem";
import { Paper, Typography, Button, Box } from "@mui/material";
import carting from '../components/Images/pngwing.com.png'
const Cart = () => {
  const { cart } = useContext(CartContext);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div style={{ display: "flex",height:"100%" , padding:"4em 5em"   }}>
        {cart.length > 0 ? (
          <>
          <Box sx={{display:"flex" , alignItems:"center" , gap:"1em" , width:"100%"}}>
            <Box sx={{height:"20em" , width:"50%" , background:"pink"}}>
              </Box>
             
          </Box>
          </>
        ) : (
          <Box sx>
          <Paper elevation={1} sx={{background:"#f5f5f5" , width:{sm:"22em" , xs:"auto"} , padding:"1.5em 1.5em"}}>
          <div style={{display:"flex" , flexDirection:"column" , justifyContent:"center" , alignItems:"center" , gap:"1em"}}>
            <img src={carting} alt="" width={300} />
            <Typography sx={{color:"#121212 " , fontWeight:"700" , fontSize:"19px"}}>Cart Empty</Typography>
            <Typography  sx={{color:"#121212" , fontWeight:"500" , fontSize:"16px" , width:"17em" , textAlign:"center"}}>Look like you haven't added anything in your cart!!!</Typography>
            <Link to={"/"}>
              <Button variant="contained" sx={{background:"#fff !important" , color:"#121212" , fontWeight:"700"}}>Shop Now</Button>
            </Link>
          </div>
          </Paper>
          </Box>
        )}
    </div>
  );
};

export default Cart;
